def how_many
  array = journalists_array
  puts array.count
end

def contain_a_number
  array = journalists_array
  array_number = []
  array.each do |number|
    a=-1
    10.times do
    a=a+1
      if number.to_s.include? "#{a}"  then
        array_number << number
      end
    end
  end
  puts array_number.uniq.count
end

def contain_aude
  array = journalists_array
  array_aude = []
  array.each do |aude|
    if aude.include?"aude" then
      array_aude << aude
    end
  end
  puts array_aude.count
end

def contain_upcase_first_letter
  array = journalists_array
  upcase_first_letter = array.each.grep(/@[[:upper:]]/)
  puts upcase_first_letter.count
end

def contain_one_upcase
  array = journalists_array
  one_upcase = array.each.grep(/[[:upper:]]/)
  puts one_upcase.count
end

def contain_underscore
    array = journalists_array
    array = array.join('')
    number_of_underscore = array.count('_')
    puts number_of_underscore
end

def alphabetic_order
  array = journalists_array
  array = array.map(&:downcase)
  puts array.sort
end

def fifty_shortest
  array = journalists_array
  array = array.sort_by(&:length)
  puts array[0..49]
end

def find_index_epenser
  array = journalists_array
  puts array.index("@epenser")
end

def perform
  puts "Entre le numéro correspondant à ta requête !"
  puts "Le nombre de journalistes : 1"
  puts "Le nombre d'handles qui contiennent un numéro : 2"
  puts "Le nombre d'handles qui contiennent 'Aude' (sans prendre en compte les majuscules) : 3"
  puts "Le nombre d'handles qui commencent par une majuscule : 4"
  puts "Le nombre d'handles qui contiennent une majuscule : 5"
  puts "Le nombre d'underscore dans tous les handles confondus : 6"
  puts "La liste des handles triés par ordre alphabétique : 7"
  puts "La liste des 50 handles les plus courts : 8"
  puts "La position d'@epenser dans l'array : 9"
  puts "Si tu souhaite sortir de cet exelllent programme : Maintiens CTRL+C"
  print ">"
  numero_entree = gets.to_i
  puts "-------------------"
  puts " "
if numero_entree.class == Integer && 1 <= numero_entree && numero_entree <= 9 then
  puts "Voici le résultat :"
  puts " "
  if numero_entree == 1 then
    how_many
  end
  if numero_entree == 2 then
    contain_a_number
  end
  if numero_entree == 3 then
    contain_aude
  end
  if numero_entree == 4 then
    contain_upcase_first_letter
  end
  if numero_entree == 5 then
    contain_one_upcase
  end
  if numero_entree == 6 then
    contain_underscore
  end
  if numero_entree == 7 then
    alphabetic_order
  end
  if numero_entree == 8 then
    fifty_shortest
  end
  if numero_entree == 9 then
    find_index_epenser
  end
  puts " "
  puts "-------------------"
  puts " "
  puts "C'est good ?"
else
  puts "METS BIEN UN CHIFFRE ENTRE 1 et 9 !"
  puts "-------------------"
end
  puts " "
  perform
end

